       adults = #{adults},
       children = #{children}
 WHERE id = #{id}

mapper.select:
SELECT rt.id, rt.name, hrt.number_of_rooms, hrt.holding_capacity AS "holding_capacity.id"
  FROM roomtypes rt JOIN hotelsroomtypes hrt ON rt.id = hrt.roomtype_id
 WHERE hrt.hotel_id = #{hotel_id}

     AND rt.id NOT IN 

         #{item}

SELECT rt.id, rt.name, hrt.number_of_rooms
  FROM roomtypes rt JOIN hotelsroomtypes hrt ON rt.id = hrt.roomtype_id
 WHERE hrt.hotel_id = #{hotel_id}

SELECT r.id, r.room_number, r.holding_capacity AS "holding_capacity.id", r.additional_bed AS "additional_bed.id", r.onsale_state AS "onsale_state.id", r.room_state AS "room_state.id" FROM rooms r WHERE r.roomtype_id = #{roomtype_id} AND r.onsale_state IN (0,1) AND r.holding_capacity >= #{adults}
AND r.additional_bed = #{additional_bed}
AND NOT EXISTS (SELECT * FROM reservationrooms rr JOIN reservations rv ON rr.reservation_id = rv.id WHERE ((COALESCE(#{check_in}::timestamp,now()::timestamp),#{check_out}::timestamp) OVERLAPS (rv.check_in,rv.check_out)) AND rr.rooms_id = r.id AND rr.roomtype_id = r.roomtype_id)
UNION SELECT r.id, r.room_number, r.holding_capacity AS "holding_capacity.id", r.additional_bed AS "additional_bed.id", r.onsale_state AS "onsale_state.id", r.room_state AS "room_state.id" FROM rooms r WHERE r.roomtype_id = #{roomtype_id} AND r.id = #{rooms_id}
ORDER BY room_number ASC

SELECT r.id, r.room_number, r.holding_capacity AS "holding_capacity.id", r.additional_bed AS "additional_bed.id", r.onsale_state AS "onsale_state.id", r.room_state AS "room_state.id", COALESCE(r.roomratesplanes_id, rts.roomratesplanes_id) AS "room_rate.id" FROM rooms r JOIN roomratesseasons rts ON r.roomtype_id = rts.roomtype_id AND ((COALESCE(#{check_in}::timestamp,now()::timestamp), #{check_out}::timestamp) OVERLAPS (date_trunc('day',rts.season_from)::timestamp, get_end_of_day(rts.season_to))) WHERE r.roomtype_id = #{roomtype_id} AND r.onsale_state IN (0,1) AND r.holding_capacity >= #{adults} AND NOT EXISTS (SELECT * FROM reservationrooms rr JOIN reservations rv ON rr.reservation_id = rv.id WHERE ((COALESCE(#{check_in}::timestamp,now()::timestamp),#{check_out}::timestamp) OVERLAPS (rv.check_in,rv.check_out)) AND rr.rooms_id = r.id AND rr.roomtype_id = r.roomtype_id)
UNION SELECT r.id, r.room_number, r.holding_capacity AS "holding_capacity.id", r.additional_bed AS "additional_bed.id", r.onsale_state AS "onsale_state.id", r.room_state AS "room_state.id", COALESCE(r.roomratesplanes_id, rts.roomratesplanes_id) AS "room_rate.id" FROM rooms r JOIN roomratesseasons rts ON r.roomtype_id = rts.roomtype_id AND ((COALESCE(#{check_in}::timestamp,now()::timestamp), #{check_out}::timestamp) OVERLAPS (date_trunc('day',rts.season_from)::timestamp(0), get_end_of_day(rts.season_to))) WHERE r.roomtype_id = #{roomtype_id} AND r.id = #{rooms_id}
ORDER BY room_number ASC

SELECT COUNT(*)
  FROM roomtypes rt JOIN hotelsroomtypes hrt ON rt.id = hrt.roomtype_id
 WHERE hrt.hotel_id = #{hotel_id}

SELECT id, room_number, holding_capacity AS "holding_capacity.id", additional_bed AS "additional_bed.id", onsale_state AS "onsale_state.id" FROM rooms r WHERE
r.onsale_state = #{onsale_state}
AND EXISTS (SELECT * FROM reservationrooms rr WHERE rr.reservation_id = #{reservation_id} AND rr.rooms_id = r.id)
r.onsale_state IN (0,1)
AND r.roomtype_id = #{roomtype_id}
AND r.hotel_id = #{hotel_id}
LIMIT #{room_count}
UNION ALL SELECT id, room_number, holding_capacity AS "holding_capacity.id", additional_bed AS "additional_bed.id", onsale_state AS "onsale_state.id" FROM rooms r WHERE id = #{rooms_id}
ORDER BY room_number ASC

