mapper.update:
UPDATE saleplanes
   SET sale = #{sale}, 
       sale_uz = #{sale_uz}, 
       update_date = now(), 
       initiator_user_id = #{initiator_user_id}
 WHERE id = #{id}

UPDATE saleplanes
   SET sale = #{sale}, 
       sale_uz = #{sale_uz}, 
       update_date = now(), 
       initiator_user_id = #{initiator_user_id}
 WHERE hotelsusers_id = #{hotelsusers_id}
   AND roomtypes_id = #{roomtypes_id}
   AND person_number = #{person_number}
   AND is_group = #{is_group}
   AND sale_date::date = #{sale_date}::date

mapper.select:
SELECT COUNT(*)         
  FROM saleplanes
 WHERE hotelsusers_id = #{hotelsusers_id}
   AND roomtypes_id = #{roomtypes_id}
   AND person_number = #{person_number}
   AND is_group = #{is_group}
   AND sale_date::date = #{sale_date}::date

mapper.update:
UPDATE saleplanes
   SET status = #{status}, 
       update_date = now(), 
       initiator_user_id = #{initiator_user_id}
 WHERE id = #{id}

mapper.select:
SELECT DISTINCT COALESCE(s.sale_date,d.date) AS sale_date, s.hotelsusers_id, s.roomtypes_id
  FROM get_date_list(#{date_from}::date, #{date_to}::date) d LEFT JOIN saleplanes s ON d.date = s.sale_date AND s.hotelsusers_id = #{hotelsusers_id}
                                                                                                            AND s.roomtypes_id = #{roomtypes_id}
ORDER BY 1 ASC

SELECT id, create_date, sale_date, sale, sale_uz, update_date, initiator_user_id, 
       hotelsusers_id, status, roomtypes_id, person_number, is_group
  FROM saleplanes
 WHERE hotelsusers_id = #{hotelsusers_id}
   AND roomtypes_id = #{roomtypes_id}
   AND is_group = #{is_group}
   AND person_number = #{person_number}
   AND sale_date::date = #{sale_date}::date

SELECT SUM(CASE status WHEN true THEN 1 ELSE 0 END)/COUNT(*) 
  FROM saleplanes
 WHERE hotelsusers_id = #{hotelsusers_id}
   AND roomtypes_id = #{roomtypes_id}
   AND is_group = #{is_group}
   AND sale_date::date = #{sale_date}::date

SELECT id, sale_date, sale, sale_uz
  FROM saleplanes
 WHERE hotelsusers_id = #{hotelsusers_id}
   AND roomtypes_id = #{roomtypes_id}
   AND status = true
   AND is_group = #{is_group}
   AND person_number = #{person_number}
   AND sale_date::date = #{sale_date}::date
   AND sale_date BETWEEN #{check_in}::date AND #{check_out}::date
