package uz.hbs.utils.email;

import java.io.Serializable;
import java.io.UnsupportedEncodingException;
import java.math.BigDecimal;
import java.util.HashMap;
import java.util.Map;

import org.antlr.stringtemplate.StringTemplate;
import org.bouncycastle.util.encoders.Base64;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import uz.hbs.MyWebApplication;
import uz.hbs.beans.AdditionalServiceDetail;
import uz.hbs.beans.AdditionalServiceOrder;
import uz.hbs.beans.AdditionalServicePrice;
import uz.hbs.beans.Message;
import uz.hbs.db.MyBatisHelper;
import uz.hbs.utils.FormatUtil;

public class CIPHallEmailNotifier {
	private static final Logger logger = LoggerFactory.getLogger(CIPHallEmailNotifier.class);

	public static void send(final long additionalServiceOrderId) {
		prepareDataFromDB(additionalServiceOrderId, false);
	}

	public static void sendCancel(final long additionalServiceOrderId) {
		prepareDataFromDB(additionalServiceOrderId, true);
	}

	private static void prepareDataFromDB(long additionalServiceOrderId, boolean isCancel) {
		AdditionalServicePrice price = new MyBatisHelper().selectOne("selectCurrentAdditionalServicePrice");
		AdditionalServiceOrder order = new MyBatisHelper().selectOne("selectAdditionalServiceOrderById", additionalServiceOrderId);

		HashMap<String, Serializable> param = new HashMap<String, Serializable>();
		param.put("additionalserviceorders_id", additionalServiceOrderId);
		param.put("air_service_type", AdditionalServiceDetail.AIR_SERVICE_TYPE_VIP_HALL);
		param.put("service_type", AdditionalServiceDetail.SERVICE_TYPE_ARRIVED);

		AdditionalServiceDetail detail = null;
		if ((Boolean) new MyBatisHelper().selectOne("selectIsExistsServiceType", param)) {
			detail = (AdditionalServiceDetail) new MyBatisHelper().selectOne("selectVIPHallGuests", param);
			if (detail != null)
				prepareHtml(additionalServiceOrderId, price, true, detail, isCancel);
		}

		param.put("service_type", AdditionalServiceDetail.SERVICE_TYPE_DEPARTED);

		if ((Boolean) new MyBatisHelper().selectOne("selectIsExistsServiceType", param)) {
			AdditionalServiceDetail detail2 = (AdditionalServiceDetail) new MyBatisHelper().selectOne("selectVIPHallGuests", param);
			if (detail2 != null)
				prepareHtml(additionalServiceOrderId, price, false, detail2, isCancel);
			else if (detail2 == null && order.getReservations_id() != null && detail != null) {
				prepareHtml(additionalServiceOrderId, price, false, detail, isCancel);
			}
		}
	}

	private static void prepareHtml(final long additionalServiceOrderId, final AdditionalServicePrice price, final boolean isArrival,
			AdditionalServiceDetail detail, boolean isCancel) {
		Map<String, Object> attributes = new HashMap<String, Object>();

		attributes.put("detail", detail);
		attributes.put("arrival_or_departure", isArrival ? "<span style=\"font-weight: bold; text-decoration: underline;\">вылета</span>/прилета"
				: "вылета/<span style=\"font-weight: bold; text-decoration: underline;\">прилета</span>");
		
		if (isCancel) {
			attributes.put("text_info", "На основании договора № 2496 от 08.10.2015 просим <strong>отменить</strong> заявку на обслуживание в Business Lounge CIP на");
		} else {
			attributes.put("text_info", "На основании договора № 2496 от 08.10.2015 просим Вас обслужить в Зале повышенной комфортности (CIP)");
		}

		MyStringTemplateGroup templateGroup = new MyStringTemplateGroup("mailgroup");
		templateGroup.setFileCharEncoding("UTF-8");
		templateGroup.registerRenderer(Double.class, FormatUtil.getDoubleRenderer());
		templateGroup.registerRenderer(BigDecimal.class, FormatUtil.getBigDecimalRenderer());

		String creator_email = new MyBatisHelper().selectOne("selectCreatorEmail", additionalServiceOrderId);

		StringTemplate st = templateGroup.getInstanceOf("uz/hbs/utils/email/templates/reservation/CIPHall.html", attributes);
		
		sendEmail(price.getEmail_send_request_green(), st.toString(), String.valueOf(additionalServiceOrderId), creator_email);
	}

	private static void sendEmail(final String recipient, final String content, final String number, String creator_email) {
		new Thread() {
			
			@Override
			public void run() {
				String serverType = MyWebApplication.getConfigBundle().getString("server_type");
				if (serverType.equals("production")) {
					serverType = "";
				} else {
					serverType = "Тест. ";
				}

				Message message = new Message();
				message.setRecipient(recipient);
				if (creator_email != null)
					message.setRecipient_cc(new String[] { creator_email });
				message.setRecipient_bcc(new String[] { "request@hotelios.uz", "maqsudjon@gmail.com" });
				message.setSubject(serverType + "Заявка на CIP Hall №" + number);
				message.setContent(content);
				try {
					message.setAttachment(Base64
							.decode(""
									.getBytes("UTF-8")));
				} catch (UnsupportedEncodingException e) {
					logger.error("Exception", e);
				}
				EmailUtil.sendHtmlEmail(message);
			};
		}.start();
	}
}
